import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";
import { Pie } from "react-chartjs-2";

// ✅ Register required elements
ChartJS.register(ArcElement, Tooltip, Legend);

import React from "react";
// import { Pie } from "react-chartjs-2";

const ExpenseChart = ({ transactions }) => {
  const categories = ["Food", "Travel", "Shopping", "Other"];
  
  const data = {
    labels: categories,
    datasets: [
      {
        data: categories.map((cat) =>
          transactions.filter((t) => t.category === cat).reduce((sum, t) => sum + t.amount, 0)
        ),
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#8A2BE2"],
      },
    ],
  };

  return <Pie id="expenseChart" data={data} options={options} />
  ;
};

export default ExpenseChart;
